import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, tap } from 'rxjs';
import { environment } from 'src/environments/environment';
import { UserWithToken } from './types/userToken';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
    private apiUrl = environment.apiUrl;
  constructor(private http: HttpClient) { }

  login(email: string, password: string) {
    return this.http.post<any>(`${this.apiUrl}/api/users/login`, { email, password })
    .pipe(
      tap(user => {
        console.log(user)
        if(user.token){
        console.log(user.token)

          sessionStorage.setItem('token', user.token);
        }
      })
    )
  }

  register(username: string, email: string, password: string, rePassword: string): Observable<UserWithToken> {
    return this.http.post<UserWithToken>(`${this.apiUrl}/api/users/register`, { username, email, password, rePassword })
      .pipe(
        tap(user => {
          if (user.token) {
            // Store the token in sessionStorage
            sessionStorage.setItem('token', user.token);
          }
        })
      );
  }

   logout() {
    sessionStorage.removeItem('token');
   }



}
