import { Component, OnInit } from '@angular/core';
import { ApiService } from 'src/app/api.service';
import { Book } from 'src/app/types/book';
import { SlicePipe } from 'src/app/shared/pipes/slice.pipe';
import { UserService } from 'src/app/user/user.service';

@Component({
  selector: 'app-books',
  templateUrl: './books.component.html',
  styleUrls: ['./books.component.css']
})
export class BooksComponent implements OnInit {
  booksList: Book[] = [];
  showFullDescription: boolean = false;
  truncatedDescriptionLength: number = 300;

  constructor(private apiService: ApiService, private userService: UserService) { }

  get isLogged(): boolean {
    return this.userService.isLogged;
  }

  ngOnInit(): void {
    this.apiService.getBooks().subscribe({
      next: (books) => {
        // console.log(books)
        // books.sort((a, b) => new Date(b['createdAt']).getTime() - new Date(a['createdAt']).getTime());

        // this.booksList = books.slice(0, 3);
        this.booksList = books;

      },
      error: (err) => {
        console.error(`Error: ${err}`);
      }
    })
  }

  toggleDescription() {
    this.showFullDescription = !this.showFullDescription;
  }


}

<div  class="container-card">
  <div class="title text-center mt-5">
    <h1 class="text-uppercase ">Books</h1>
  </div>
  <div *ngFor="let book of booksList" class="card mb-3 shadow-effect mx-5">
    <div class="row g-0 " >
      <div class=" img-card col-md-2">
        <img src={{book.image}} alt="Girl in a jacket" class="img-fluid founded-start" width="150" height="150">
      </div>
      <div class=" description-card col-md-10" >
        <div class="card-body">
          <h3 class="card-title">{{book.name}}</h3>
          <h5 class="card-author">by {{book.author}}</h5>
          <p class="card-text">

            {{ showFullDescription ? book.description : (book.description | slice: 0: 300)+ '...'  }}

          </p>
          <button *ngIf="isLogged" class="btn-details" [routerLink]="['/books', book._id]">Details</button>
        </div>
      </div>
    </div>
  </div>
</div>


