import { Component, OnInit } from '@angular/core';
import { ApiService } from 'src/app/api.service';
import { Book } from 'src/app/types/book';

import { UserService } from 'src/app/user/user.service';

@Component({
  selector: 'app-books',
  templateUrl: './books.component.html',
  styleUrls: ['./books.component.css']
})
export class BooksComponent implements OnInit {
  booksList: Book[] = [];


  constructor(private apiService: ApiService, private userService: UserService) { }

  get isLogged(): boolean {
    return this.userService.isLogged;
  }

  ngOnInit(): void {
    this.apiService.getBooks().subscribe({      
      next: (books) => {

        this.booksList = books;

      },
      error: (err) => {
        console.error(`Error: ${err}`);
      }
    })
  }


}

<div  class="container-card">
  <div class="title text-center">
    <h1 class="text-uppercase my-5">ACOTAR</h1>
  </div>
  <div *ngFor="let book of booksList" class="card mb-3 shadow-effect ">
    <div class="row g-0">
      <div class=" img-card col-md-4">
        <img src={{book.image}} alt="Girl in a jacket" class="img-fluid founded-start" width="150" height="150">
      </div>
      <div class=" description-card col-md-8">
        <div class="card-body">
          <h3 class="card-title">{{book.author}}</h3>
          <p class="card-text">{{book.description}}</p>
          <button *ngIf="isLogged" class="btn-theme" [routerLink]="['/books', book._id]">Details</button>
        </div>
      </div>
    </div>
  </div>
</div>


