import { Component, OnInit } from '@angular/core';
import { ApiService } from 'src/app/api.service';
import { Book } from 'src/app/types/book';

import { UserService } from 'src/app/user/user.service';

@Component({
  selector: 'app-book-card',
  templateUrl: './book-card.component.html',
  styleUrls: ['./book-card.component.css']
})
export class BookCardComponent implements OnInit{

  booksList: Book[] = [];


  constructor(private apiService: ApiService, private userService: UserService) { }

  get isLogged(): boolean {
    return this.userService.isLogged;
  }

  ngOnInit(): void {
    this.apiService.getBooks().subscribe({
      next: (books) => {

        books.sort((a, b) => new Date(b['createdAt']).getTime() - new Date(a['createdAt']).getTime());

        this.booksList = books.slice(0, 3);


      },
      error: (err) => {
        console.error(`Error: ${err}`);
      }
    })
  }

}
<div  class="container-card mt-5">
    <div class="title text-center ">
      <h1 class="text-uppercase ">What is new?</h1>
    </div>
    <div *ngFor="let book of booksList" class="card mb-3 shadow-effect mx-5 row align-items-center">
      <div class="col-lg-3" >
        <div class=" img-card">
          <img src={{book.image}} alt="Girl in a jacket" class="img-fluid founded-start" width="150" height="150">
        </div>
        <div class=" description-card " >
          <div class="card-body">
            <h3 class="card-title">{{book.name}}</h3>
            <h5 class="card-author">by {{book.author}}</h5>

            <button *ngIf="isLogged" class="btn-details shadow-effect" [routerLink]="['/books', book._id]">Details</button>
          </div>
        </div>
      </div>
    </div>
  </div>
