import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ApiService } from 'src/app/api.service';
import { Book } from 'src/app/types/book';

@Component({
  selector: 'app-edit-book',
  templateUrl: './edit-book.component.html',
  styleUrls: ['./edit-book.component.css']
})
export class EditBookComponent implements OnInit{

  book: Book | undefined;

  constructor(private apiService: ApiService, private activatedRoute: ActivatedRoute, private router: Router) {}

  ngOnInit(): void {
    this.fetchBook();
  }


  fetchBook(): void {
    const id = this.activatedRoute.snapshot.params['id'];    

    this.apiService.getBook(id).subscribe((book) => {
      this.book = book;
    });
  }

  onSubmit(): void {
    if (this.book) {      
      // Call the ApiService to update the book
      this.apiService.updateBook(this.book).subscribe((updatedBook) => {
        this.book = updatedBook;
        // Optionally, you can navigate back to the book details page after successful update
        this.router.navigate(['/books', this.book._id]);
      });
    }
  }
}

