import { Component, OnInit } from '@angular/core';
import { ApiService } from '../api.service';
import { Comment } from '../types/comment';

@Component({
  selector: 'app-comments',
  templateUrl: './comments.component.html',
  styleUrls: ['./comments.component.css']
})
export class CommentsComponent implements OnInit{
   commentsList: Comment[] = [];
   thereAreNoComments: boolean = false;

   constructor(private apiService: ApiService) {}

   ngOnInit(): void {
     this.apiService.getComments(5).subscribe({
      next: (books) => {
        this.commentsList = books;

        if(this.commentsList.length === 0){
          this.thereAreNoComments = true;
        }
      }
     })
   }
}
<aside>
    <div class="aside">
        <h2 > Last Comments </h2>
        <div *ngIf="thereAreNoComments">
            <h3> Be first to leave a comment!</h3>
        </div>

        <div>
            <div  *ngFor="let comment of commentsList" class="aside-book">
                <a href="#">
                    <h3>{{ comment.bookId.name }}</h3>
                    <p>{{ comment.userId.username }}</p>
                    <p>
                      <span>{{ comment.createdAt }}</span>
                    </p>
                  </a>
            </div>
        </div>
    </div>
</aside>
