import { Component, OnInit } from '@angular/core';
import { ApiService } from '../api.service';
import { Comment } from '../types/comment';
import { Book } from '../types/book';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-comments',
  templateUrl: './comments.component.html',
  styleUrls: ['./comments.component.css']
})
export class CommentsComponent implements OnInit {
  commentsList: Comment[] = [];
  thereAreNoComments: boolean = false;

  constructor(private apiService: ApiService, private route: ActivatedRoute) { }

  ngOnInit(): void {
    const bookId = this.route.snapshot.paramMap.get('id');
    console.log(bookId)

    if (bookId) {
      this.apiService.getBookComments(bookId).subscribe((comments) => {
       console.log(comments)
        this.commentsList = comments;

        if (this.commentsList.length === 0) {
          this.thereAreNoComments = true;
        }
      });
    }


  }
}


<aside>
    <div class="aside">

        <div *ngIf="thereAreNoComments">
            <h3> Be first to leave a comment!</h3>
        </div>

        <div>
            <h2 > Comments </h2>
            <div  *ngFor="let comment of commentsList" class="aside-book">
                <a href="#">
                    <h3>{{ comment.bookName }}</h3>
                    <p>{{ comment.username }}</p>
                    <p>

                    </p>
                  </a>
            </div>
        </div>
    </div>
</aside>
