import { Component, OnInit } from '@angular/core';
import { ApiService } from 'src/app/api.service';
import { Book } from 'src/app/types/book';

import { UserService } from 'src/app/user/user.service';

@Component({
  selector: 'app-books',
  templateUrl: './books.component.html',
  styleUrls: ['./books.component.css']
})
export class BooksComponent implements OnInit {
  booksList: Book[] = [];


  constructor(private apiService: ApiService, private userService: UserService) { }

  get isLogged(): boolean {
    return this.userService.isLogged;
  }

  ngOnInit(): void {
    this.apiService.getBooks().subscribe({      
      next: (books) => {

        this.booksList = books;

      },
      error: (err) => {
        console.error(`Error: ${err}`);
      }
    })
  }


}


<div class="theme-title" >
  <div *ngFor="let book of booksList" class="theme-container mt-5 mb-5 mb-3 ">
    <!-- <div class="theme-name-wrapper col-md-4 mb-3 "> -->
      <div class="theme-name">
        <a [routerLink]="book._id" class="normal">
            <img src={{book.image}} alt="Girl in a jacket" width="100" height="150">
          <h4>{{ book.name}}</h4>
        </a>
        <div class="columns">
          <div>
            <p>Date: <time>{{ book.published }}</time></p>
            <div class="nick-name">
              <p>Username: <span>Username</span></p>
            </div> 
          </div>
          <div class="subscribers">
            <button *ngIf="isLogged" class="subscribe" [routerLink]="['/books', book._id]">Details</button>
            <p>Subscribers: <span>Subscribers</span></p>
          </div>
        </div>
      </div>
    </div>
  </div>
<!-- </div> -->
