import { Component, OnInit } from '@angular/core';
import { ApiService } from 'src/app/api.service';
import { Book } from 'src/app/types/book';
import { UserService } from 'src/app/user/user.service';

@Component({
  selector: 'app-books',
  templateUrl: './books.component.html',
  styleUrls: ['./books.component.css']
})
export class BooksComponent implements OnInit{
 booksList: Book[] = [];


 constructor(private apiService: ApiService, private userService: UserService){}

 get isLogged(): boolean {
  return this.userService.isLogged;
 }

 ngOnInit(): void {
   this.apiService.getBooks().subscribe({
    next: (books: Book[]) => {
      console.log(books)
      this.booksList = books;     

    },
    error: (err) => {
      console.error(`Error: ${err}`);
    }
   })


 }
}


<div class="theme-title" >
  <div *ngFor="let book of booksList" class="theme-container">
    <div class="theme-name-wrapper">
      <div class="theme-name">
        <a [routerLink]="book._id" class="normal">
          <h2>{{ book.name | slice : 25 }}</h2>
        </a>
        <div class="columns">
          <div>
            <p>
              Date: <time>{{ book.published }}</time>
            </p>
            <!-- <div class="nick-name">
              <p>
                Username: <span>{{ book.userId.username }}</span>
              </p>
            </div> -->
          </div>
          <!-- <div class="subscribers">
            <button *ngIf="isLogged" class="subscribe">Subscribe</button>
            <p>
              Subscribers: <span>{{ theme.subscribers.length }}</span>
            </p>
          </div> -->
        </div>
      </div>
    </div>
  </div>
</div>
